package com.spacexdata.adapter;import android.content.Context;import android.media.Image;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.spacexdata.pojo.Repo;import com.spacexdata.retrofit.R;import com.squareup.picasso.Callback;import com.squareup.picasso.Picasso;import java.util.List;import static android.content.ContentValues.TAG;public class RecyclerAdapter extends RecyclerView.Adapter<RecyclerAdapter.myViewHolder> {    private Context context;    private List<Repo> repoList;    public RecyclerAdapter(Context context, List<Repo> repos) {        this.context = context;        this.repoList=repos;    }    @Override    public myViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View view= LayoutInflater.from(parent.getContext()).inflate(R.layout.list_launches,parent,false);        return new myViewHolder(view);    }    @Override    public void onBindViewHolder(final myViewHolder holder, int position) {        Repo repo=repoList.get(position);        holder.textViewName.setText("Flight Number: "+repo.flightNumber.toString());        holder.textViewDate.setText("Launch Year: "+repo.launchYear.toString());        holder.textViewDetails.setText("Detail: "+repo.details);        Picasso.with(context).load(repo.links.missionPatch.toString()).error(R.mipmap.ic_launcher).resize(150,150)                .into(holder.ımageView, new Callback() {                    @Override                    public void onSuccess() {                    }                    @Override                    public void onError() {                        Log.d(TAG, "onError: Image ");                    }                });    }    @Override    public int getItemCount() {        return repoList.size();    }    public class myViewHolder  extends RecyclerView.ViewHolder{        TextView textViewName,textViewDate,textViewDetails;        ImageView ımageView;        public myViewHolder(View itemView) {            super(itemView);            textViewName=(TextView)itemView.findViewById(R.id.textViewHeader);            textViewDate=(TextView)itemView.findViewById(R.id.textViewDate);            textViewDetails=(TextView)itemView.findViewById(R.id.textViewDetail);            ımageView=(ImageView)itemView.findViewById(R.id.imageView);        }    }}