package com.spacexdata.pojo;import com.google.gson.annotations.Expose;import com.google.gson.annotations.SerializedName;import java.util.List;public class Payload {    @SerializedName("payload_id")    @Expose    public String payloadId;    @SerializedName("cap_serial")    @Expose    public String capSerial;    @SerializedName("reused")    @Expose    public Boolean reused;    @SerializedName("customers")    @Expose    public List<String> customers = null;    @SerializedName("payload_type")    @Expose    public String payloadType;    @SerializedName("payload_mass_kg")    @Expose    public Integer payloadMassKg;    @SerializedName("payload_mass_lbs")    @Expose    public Double payloadMassLbs;    @SerializedName("orbit")    @Expose    public String orbit;    @SerializedName("mass_returned_kg")    @Expose    public Object massReturnedKg;    @SerializedName("mass_returned_lbs")    @Expose    public Object massReturnedLbs;    @SerializedName("flight_time_sec")    @Expose    public Object flightTimeSec;    @SerializedName("cargo_manifest")    @Expose    public String cargoManifest;}